import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PumpDetail = () => {
  const { id } = useParams(); // Get the pump ID from the URL
  const [pump, setPump] = useState(null); // Initialize pump as null
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch pump details from the backend
    fetch(`http://localhost:5001/api/pumps/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch pump details');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched pump details:', data); // Debugging
        setPump(data); // Set the pump data
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching pump details:', error);
        setError('Failed to fetch pump details.');
        setLoading(false);
      });
  }, [id]); // Dependency array ensures it fetches data when `id` changes

  if (loading) {
    return <div>Loading pump details...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!pump) {
    return <div>No pump details available.</div>;
  }

  return (
    <div className="pump-detail">
      <h2>{pump.name}</h2>
      <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
      <p>Price: {pump.price}</p>
      <h3>Features</h3>
      <ul>
        {pump.features && pump.features.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </ul>
      <img src={`http://localhost:5001/uploads/${pump.rightImage}`} alt={`${pump.name} Details`} />
      <div>
        <p>
          <strong>Previous:</strong> {pump.previousId ? <a href={`/pumpDetail/${pump.previousId}`}>{pump.previousId}</a> : 'N/A'}
        </p>
        <p>
          <strong>Next:</strong> {pump.nextId ? <a href={`/pumpDetail/${pump.nextId}`}>{pump.nextId}</a> : 'N/A'}
        </p>
      </div>
    </div>
  );
};

export default PumpDetail;
