import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
    const { pumpId } = useParams(); // Extract pumpId from URL
    const navigate = useNavigate();

    const [pump, setPump] = useState(null); // State to hold pump data
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    // Fetch pump details based on the pumpId
    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                console.log('Fetching details for pumpId:', pumpId);

                const response = await fetch(`http://localhost:5001/api/pumps/${pumpId}`);

                if (!response.ok) {
                    throw new Error('Pump not found');
                }

                const data = await response.json();
                setPump(data.pump);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPumpDetails(); // Fetch details for the current pump
    }, [pumpId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}. Please go back to the <a href="/products">products page</a>.</div>;
    }

    if (!pump) {
        return <div>Pump not found. Please go back to the <a href="/products">products page</a>.</div>;
    }

    // Ensure pump.features is an array before using map
    const features = Array.isArray(pump.features) ? pump.features : [];

    // Navigation logic
    const pumpIds = ['v4', 'v5', 'v6']; // Example list of pump IDs for navigation
    const currentIndex = pumpIds.indexOf(pumpId);
    const prevPump = pumpIds[(currentIndex - 1 + pumpIds.length) % pumpIds.length];
    const nextPump = pumpIds[(currentIndex + 1) % pumpIds.length];

    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`);
    };

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                {/* Ensure image URL is correct */}
                <img 
                    src={pump.image ? `http://localhost:5001/uploads/${pump.image}` : 'default_image.jpg'} 
                    className="product-image" 
                    alt={pump.name || 'Pump'} 
                />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <div>
                            {/* Render features only if the array is valid */}
                            {features.length > 0 ? (
                                <ul>
                                    {features.map((feature, index) => (
                                        <li key={index}>{feature}</li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No features available.</p>
                            )}
                        </div>
                    </div>

                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Image for right view of the pump */}
            <div className="chart">
                <img 
                    src={pump.rightImage ? `http://localhost:5001/uploads/${pump.rightImage}` : 'default_image.jpg'} 
                    alt="Right view of pump" 
                />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump)}
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump)}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>
        </div>
    );
};

export default PumpDetail;
