import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPumps(data.pumps);
        } else {
          setError('Failed to load pumps.');
        }
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to load pumps.');
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h2>Residential Submersible Pumps</h2>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="gallery">
          {pumps.map((pump) => (
            <div key={pump.id}>
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <p>{pump.name}</p>
              <Link to={`/pumpDetail/${pump.id}`}>Read more</Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
