import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PumpDetail = () => {
  const { id } = useParams(); // Get pump ID from the URL
  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5001/api/pumps/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setPump(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching pump details:', error);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!pump) {
    return <div>Failed to load pump details.</div>;
  }

  return (
    <div>
      <h1>{pump.name}</h1>
      <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
      <p><strong>Price:</strong> {pump.price}</p>
      <h3>Features</h3>
      <ul>
        {pump.features.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </ul>
    </div>
  );
};

export default PumpDetail;
