import React, { useState, useEffect } from 'react';
import { Alert } from '@/components/ui/alert';

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [currentView, setCurrentView] = useState('residential');
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [selectedPump, setSelectedPump] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false);

  const initialFormState = {
    id: '',
    name: '',
    price: '',
    features: '',
    image: null,
    rightImage: null
  };

  const [formData, setFormData] = useState(initialFormState);

  // Fetch pumps data
  const fetchPumps = async () => {
    try {
      setLoading(true);
      const endpoint = currentView === 'residential' ? '/api/pumps' : '/api/agpumps';
      const response = await fetch(`http://localhost:5001${endpoint}`);
      if (!response.ok) throw new Error('Failed to fetch pumps');
      const data = await response.json();

      if (currentView === 'residential') {
        setResidentialPumps(data.pumps);
      } else {
        setAgriculturalPumps(data.pumps);
      }
    } catch (error) {
      setError('Failed to fetch pumps');
      console.error('Error fetching pumps:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPumps();
  }, [currentView]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle file selection
  const handleFileChange = (e, fileType) => {
    setFormData(prev => ({
      ...prev,
      [fileType]: e.target.files[0]
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    setLoading(true);

    try {
      const formDataToSend = new FormData();
      Object.keys(formData).forEach(key => {
        if (key === 'features') {
          formDataToSend.append(key, JSON.stringify(formData[key].split(',').map(f => f.trim())));
        } else if (formData[key] !== null) {
          formDataToSend.append(key, formData[key]);
        }
      });

      const endpoint = currentView === 'residential' ? '/api/pumps' : '/api/agpumps';
      const url = `http://localhost:5001${endpoint}${isEditing ? `/${selectedPump.id}` : ''}`;
      const method = isEditing ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        body: formDataToSend
      });

      if (!response.ok) throw new Error('Failed to save pump');

      setSuccess(isEditing ? 'Pump updated successfully!' : 'Pump added successfully!');
      setShowForm(false);
      setFormData(initialFormState);
      setIsEditing(false);
      setSelectedPump(null);
      fetchPumps();
    } catch (error) {
      setError(error.message || 'An error occurred');
      console.error('Error submitting form:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle pump deletion
  const handleDelete = async (pumpId) => {
    if (!window.confirm('Are you sure you want to delete this pump?')) return;

    try {
      setLoading(true);
      const endpoint = currentView === 'residential' ? '/api/pumps' : '/api/agpumps';
      const response = await fetch(`http://localhost:5001${endpoint}/${pumpId}`, {
        method: 'DELETE'
      });

      if (!response.ok) throw new Error('Failed to delete pump');

      setSuccess('Pump deleted successfully!');
      fetchPumps();
    } catch (error) {
      setError('Failed to delete pump');
      console.error('Error deleting pump:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle editing pump
  const handleEdit = (pump) => {
    setSelectedPump(pump);
    setFormData({
      id: pump.id,
      name: pump.name,
      price: pump.price,
      features: pump.features.join(', '),
      image: null,
      rightImage: null
    });
    setIsEditing(true);
    setShowForm(true);
  };

  // Handle pump details navigation
  const handlePumpDetails = (pumpId) => {
    // Instead of using Link, we'll pass this to a parent component via props
    // or use a custom routing solution
    console.log(`Navigate to pump details: ${pumpId}`);
  };

  return (
    <div className="admin-portal">
      <h1 className="text-3xl font-bold mb-6">Admin Portal</h1>

      {/* Alerts */}
      {error && (
        <Alert variant="destructive" className="mb-4">
          {error}
        </Alert>
      )}
      {success && (
        <Alert className="mb-4 bg-green-100">
          {success}
        </Alert>
      )}

      {/* View Toggle */}
      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
      </div>

      {/* Add New Pump Button */}
      <div className="gallery-container">
        <div className="add-new-pump" onClick={() => setShowForm(true)}>
          <span className="plus-sign">+</span>
          <p className="add-text">Add New Pump</p>
        </div>
      </div>

      {/* Pump Form Modal */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full">
            <h2 className="text-2xl font-bold mb-4">
              {isEditing ? 'Edit Pump' : 'Add New Pump'}
            </h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block mb-1">ID</label>
                <input
                  type="text"
                  name="id"
                  value={formData.id}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                  disabled={isEditing}
                />
              </div>
              <div>
                <label className="block mb-1">Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
              <div>
                <label className="block mb-1">Price</label>
                <input
                  type="number"
                  name="price"
                  value={formData.price}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
              <div>
                <label className="block mb-1">Features (comma-separated)</label>
                <textarea
                  name="features"
                  value={formData.features}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
              <div>
                <label className="block mb-1">Main Image</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'image')}
                  className="w-full p-2 border rounded"
                  accept="image/*"
                  required={!isEditing}
                />
              </div>
              <div>
                <label className="block mb-1">Right Image</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'rightImage')}
                  className="w-full p-2 border rounded"
                  accept="image/*"
                  required={!isEditing}
                />
              </div>
              <div className="flex gap-4">
                <button
                  type="submit"
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                  disabled={loading}
                >
                  {loading ? 'Processing...' : isEditing ? 'Update Pump' : 'Add Pump'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowForm(false);
                    setFormData(initialFormState);
                    setIsEditing(false);
                    setSelectedPump(null);
                  }}
                  className="bg-gray-300 px-4 py-2 rounded"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Pump Gallery */}
      <div className="gallery-container">
        <div className="pump-gallery">
          {currentView === 'residential' ? (
            <>
              <h2>Residential Pumps</h2>
              {residentialPumps.length > 0 ? (
                residentialPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img 
                      src={`http://localhost:5001/uploads/${pump.image}`} 
                      alt={pump.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.jpg';
                      }}
                    />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <div className="flex gap-2">
                        <button 
                          onClick={() => handlePumpDetails(pump.id)}
                          className="bg-blue-500 text-white px-3 py-1 rounded"
                        >
                          Details
                        </button>
                        <button 
                          className="bg-yellow-500 text-white px-3 py-1 rounded"
                          onClick={() => handleEdit(pump)}
                        >
                          Edit
                        </button>
                        <button 
                          className="bg-red-500 text-white px-3 py-1 rounded"
                          onClick={() => handleDelete(pump.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Residential Pumps...</p>
              )}
            </>
          ) : (
            <>
              <h2>Agricultural Pumps</h2>
              {agriculturalPumps.length > 0 ? (
                agriculturalPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img 
                      src={`http://localhost:5001/uploads/${pump.image}`} 
                      alt={pump.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.jpg';
                      }}
                    />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <div className="flex gap-2">
                        <button 
                          onClick={() => handlePumpDetails(pump.id)}
                          className="bg-blue-500 text-white px-3 py-1 rounded"
                        >
                          Details
                        </button>
                        <button 
                          className="bg-yellow-500 text-white px-3 py-1 rounded"
                          onClick={() => handleEdit(pump)}
                        >
                          Edit
                        </button>
                        <button 
                          className="bg-red-500 text-white px-3 py-1 rounded"
                          onClick={() => handleDelete(pump.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Agricultural Pumps...</p>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPortal;
