import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../css/Admin.css';  // Ensure your CSS file is included

const AgriculturalPumps = ({ handleDeletePump }) => {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAgriculturalPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/agpumps');
        setPumps(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching agricultural pumps:', error);
        setError('Failed to load data.');
        setLoading(false);
      }
    };

    fetchAgriculturalPumps();
  }, []);

  if (loading) {
    return <p>Loading Agricultural Pumps...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="pump-gallery-container">
      <h2>Agricultural Pumps</h2>
      <div className="pump-gallery">
        {pumps.map((pump) => (
          <div key={pump.id} className="pump-item">
            <img
              src={`http://localhost:5001/uploads/${pump.image}`}
              alt={pump.name}
              className="pump-image"
            />
            <div className="pump-info">
              <h3 className="pump-name">{pump.name}</h3>
              <p className="pump-price">{pump.price}</p>
              <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">Read more</Link>
              <button
                className="delete-btn"
                onClick={() => handleDeletePump(pump.id, 'agricultural')}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AgriculturalPumps;
