// src/components/PumpDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PumpDetail = () => {
  const { pumpId } = useParams(); // Grab the pumpId from the URL parameters
  const [pump, setPump] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPumpDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/pumps/${pumpId}`);
        const data = await response.json();

        if (response.ok && data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found or an error occurred.');
        }
      } catch (error) {
        setError('Failed to fetch pump details.');
      } finally {
        setLoading(false); // Set loading to false once the request is complete
      }
    };

    fetchPumpDetails();
  }, [pumpId]);

  // Handle loading, error, and displaying pump details
  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!pump) {
    return <p>Pump details not available.</p>;
  }

  return (
    <div className="pump-detail">
      <h1>{pump.name}</h1>
      <img
        src={`http://localhost:5001/uploads/${pump.image}`}
        alt={pump.name}
        style={{ width: '100%', height: 'auto' }}
      />
      <p><strong>Price:</strong> ${pump.price}</p>

      <h3>Features:</h3>
      <ul>
        {pump.features && pump.features.length > 0 ? (
          pump.features.map((feature, idx) => (
            <li key={idx}>{feature}</li>
          ))
        ) : (
          <p>No features available for this pump.</p>
        )}
      </ul>

      {/* Optional: Add a back button */}
      <button onClick={() => window.history.back()} style={{ marginTop: '20px' }}>
        Back to Pump List
      </button>
    </div>
  );
};

export default PumpDetail;
