import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/styles.css'; // Make sure your CSS is correct

function Home() {
  const [pumps, setPumps] = useState([]); // State to hold the pumps

  // Fetch pumps from the backend API
  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => response.json())
      .then((data) => {
        setPumps(data); // Set pumps data to state
      })
      .catch((error) => console.error('Error fetching pumps:', error));
  }, []); // Empty dependency array means this runs only once when the component mounts

  return (
    <div className="main-content">
      <h2 className="section-title">Residential Submersible Pump</h2>
      <div className="pump-gallery">
        {pumps.length > 0 ? (
          pumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">Read more</Link>
              </div>
            </div>
          ))
        ) : (
          <p>No pumps available</p>
        )}
      </div>
    </div>
  );
}

export default Home;
