import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
    const { pumpId } = useParams();
    const [pump, setPump] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetch(`http://localhost:5001/api/pumps/${pumpId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.success) {
                    setPump(data.pump);
                } else {
                    setError('Failed to load pump details.');
                }
                setLoading(false);
            })
            .catch((err) => {
                console.error('Error fetching pump:', err);
                setError('Failed to load pump details.');
                setLoading(false);
            });
    }, [pumpId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="body">
            <div className="product-description">
                <img src={`http://localhost:5001/uploads/${pump.image}`} className="product-image" alt={pump.name} />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>
                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {pump.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PumpDetail;
