import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/styl.css';

const Products = () => {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Fetch agpumps data from the API
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/agpumps'); // API endpoint for agpumps collection
        if (!response.ok) {
          throw new Error('Failed to fetch agpumps data');
        }
        const data = await response.json();
        setPumps(data.agpumps || []); // Safely set agpumps data
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchPumps(); // Fetch agpumps data on mount
  }, []); // Empty dependency array ensures it runs only once on component mount

  // If loading, display a loading message
  if (loading) {
    return <div>Loading...</div>;
  }

  // If there is an error fetching the data
  if (error) {
    return <div>Error: {error}. Please try again later.</div>;
  }

  return (
    <div>
      <div className="main-content" id="mainContent">
        <div className="product-info">
          <div className="container1">
            <div>
              <h2>Agricultural Products</h2>
            </div>
            <div>
              <p>We offer a wide range of Agricultural submersible pumps to suit your needs.</p>
            </div>
          </div>
        </div>

        <div className="pump-gallery" id="pumpGallery">
          {pumps.map((pump, index) => (
            <div key={index} className="pump-item animate-on-scroll">
              {/* Assuming the image URL is stored in the pump object */}
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Products;
