// PumpDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './read.css'

import v4 from './images/v4.png';

const importAll = (r) => {
    let images = {};
    r.keys().forEach((item) => { 
        images[item.replace('./', '')] = r(item); 
    });
    return images;
};

// Import images from the specified folder
const images = importAll(require.context('./images/read', false, /\.(png|jpe?g|svg)$/));

const pumpData = {
  v4: {
    name: 'Rust free V4- Stainless Steel Pump',
    price: 'MRP: ₹16,975.00 (Inclusive of all taxes)',
    features: [
      'Rust free operation due to Stainless Steel construction',
      'Low power consumption',
      'Higher performance',
    ],
    image: images['v4.png'],
  },
  // Add other pump details here
  v42: {
    name: 'Rust free V4- Stainless Steel Pump',
    price: 'MRP: ₹16,250.00 (Inclusive of all taxes)',
    features: [
      'Rust free operation due to Stainless Steel construction',
      'Low power consumption',
      'Higher performance',
    ],
    image: 'images/v42.png',
  },
  // Continue for other pumps...
};

function PumpDetails() {
  const { id } = useParams();
  const pump = pumpData[id];

  if (!pump) {
    return <h2>Pump not found!</h2>;
  }

  return (
    <div className="product-description">
      <img src={v4} className="product-image" alt={pump.name} />
      <div>
        <h1>{pump.name}</h1>
        <p><strong>{pump.price}</strong></p>
        <div className="features">
          <h2>Features</h2>
          <ul>
            {pump.features.map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
        </div>
        <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
        <div className="image-right">
          <img src="images/read/v4table.png" alt="Image right" width="700px" />
        </div>
      </div>
    </div>
  );
}

export default PumpDetails;
