import React, { useState, useEffect } from 'react';
import axios from 'axios';
//import '../css/admin.css'; // Ensure you have CSS for styling

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({
    name: '',
    price: '',
    features: '',
    image: null,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  // Fetch all pumps
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => {
        setPumps(response.data.pumps);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch pumps');
        setLoading(false);
      });
  }, []);

  // Handle adding a new pump
  const handleAddPump = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newPump.name);
    formData.append('price', newPump.price);
    formData.append('features', newPump.features);
    if (newPump.image) {
      formData.append('image', newPump.image);
    }

    axios
      .post('http://localhost:5001/api/pumps', formData)
      .then((response) => {
        setMessage('Pump added successfully!');
        setPumps([...pumps, response.data.pump]);
        setNewPump({ name: '', price: '', features: '', image: null });
      })
      .catch((err) => {
        setError('Failed to add pump');
      });
  };

  // Handle pump deletion
  const handleDeletePump = (pumpId) => {
    if (window.confirm('Are you sure you want to delete this pump?')) {
      axios
        .delete(`http://localhost:5001/api/pumps/${pumpId}`)
        .then(() => {
          setPumps(pumps.filter((pump) => pump._id !== pumpId));
          setMessage('Pump deleted successfully!');
        })
        .catch((err) => {
          setError('Failed to delete pump');
        });
    }
  };

  if (loading) {
    return <div>Loading pumps...</div>;
  }

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      {/* Add new pump form */}
      <form onSubmit={handleAddPump}>
        <h2>Add New Pump</h2>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={newPump.name}
            onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="number"
            value={newPump.price}
            onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Features (comma separated):</label>
          <input
            type="text"
            value={newPump.features}
            onChange={(e) => setNewPump({ ...newPump, features: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Image:</label>
          <input
            type="file"
            onChange={(e) => setNewPump({ ...newPump, image: e.target.files[0] })}
            required
          />
        </div>
        <button type="submit">Add Pump</button>
      </form>

      {/* Display success or error message */}
      {message && <div className="message success">{message}</div>}
      {error && <div className="message error">{error}</div>}

      {/* Display list of pumps */}
      <h2>All Pumps</h2>
      <ul>
        {pumps.map((pump) => (
          <li key={pump._id}>
            <img
              src={`http://localhost:5001/uploads/${pump.image}`}
              alt={pump.name}
              className="pump-image"
            />
            <div>
              <h3>{pump.name}</h3>
              <p>{pump.price} INR</p>
              <p>Features: {pump.features.join(', ')}</p>
              <button onClick={() => handleDeletePump(pump._id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPortal;
