import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import bg1 from '../images/PUMP.jpg'; // Your background image
import '../css/styles.css';

function Home() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [pumps, setPumps] = useState([]); // State to hold the pumps

  // Set up the image slider
  const images = [bg1];
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval); // Clear the interval on component unmount
  }, [images.length]);

  // Fetch pumps from the backend API
  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched pumps:', data); // Log the response
        if (data.success) {
          setPumps(data.pumps); // Update the state with the pumps
        } else {
          setError('Failed to load pumps.');
        }
        setLoading(false); // Stop loading
      })
      .catch((error) => {
        console.error('Error fetching pumps:', error);
        setError('Failed to load pumps.');
        setLoading(false); // Stop loading even in case of an error
      });
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div className="main-content">
      {/* Main Image Slider */}
      <div className="image-section1">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      {/* Description Section */}
      <div className="b">
        <center>
          <p className="b2">
            Explore our range of Agricultural submersible pumps <br />
            The product range covers a wide application such as agriculture,
            water supply to townships, Industries, sewage and drainage.
          </p>
          <Link to="/products" className="btn">View Products</Link>
        </center>
      </div>

      {/* Pumps Section */}
      <h2 className="section-title">Residential Submersible Pumps</h2>
      <div className="pump-gallery">
        {loading ? (
          <div>Loading pumps...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          pumps.length > 0 ? (
            pumps.map((pump) => (
              <div key={pump._id} className="pump-item">
                {/* Construct the full image URL using the filename */}
                <img 
                  src={`http://localhost:5001/uploads/${pump.image}`} 
                  alt={pump.name} 
                  className="pump-image" 
                />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))
          ) : (
            <div>No pumps available.</div>
          )
        )}
      </div>

      {/* Additional sections for about or footer */}
      <div className="about-section">
        <center>
          <h3>About our company</h3>
          <p>The product range covers a wide application...</p>
        </center>
      </div>
    </div>
  );
}

export default Home;
