import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../css/Admin.css'; // Ensure the CSS path is correct

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [image, setImage] = useState('');
  const [rightImage, setRightImage] = useState('');
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('residential');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPumpData, setNewPumpData] = useState({
    id: '',
    name: '',
    price: '',
    features: '',
  });

  // Fetch residential and agricultural pumps
  useEffect(() => {
    fetchPumps('residential', setResidentialPumps);
    fetchPumps('agricultural', setAgriculturalPumps);
  }, []);

  const fetchPumps = async (type, setter) => {
    const endpoint = type === 'residential' ? 'pumps' : 'agpumps';
    try {
      const response = await axios.get(`http://localhost:5001/api/${endpoint}`);
      setter(response.data);
    } catch (err) {
      console.error(`Error fetching ${type} pumps:`, err);
    }
  };

  // Handle file upload
  const handleFileUpload = async (e, type) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append(type, file);

    try {
      const response = await axios.post(
        `http://localhost:5001/api/uploads/${type}`,
        formData
      );
      if (type === 'image') setImage(response.data.filePath);
      else if (type === 'rightImage') setRightImage(response.data.filePath);
    } catch (err) {
      console.error(`Error uploading ${type}:`, err);
      setError(`Error uploading ${type}.`);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPumpData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Add a new pump
  const handleAddPump = async (e) => {
    e.preventDefault();
    const endpoint = currentView === 'residential' ? 'pumps' : 'agpumps';

    const pumpData = {
      ...newPumpData,
      id: newPumpData.id || Date.now().toString(),
      features: newPumpData.features.split(',').map((feature) => feature.trim()),
      image,
      rightImage,
    };

    try {
      await axios.post(`http://localhost:5001/api/${endpoint}`, pumpData);
      setShowAddForm(false);
      setImage('');
      setRightImage('');
      setNewPumpData({ id: '', name: '', price: '', features: '' });
      fetchPumps(currentView, currentView === 'residential' ? setResidentialPumps : setAgriculturalPumps);
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  // Delete a pump
  const handleDeletePump = async (id) => {
    if (!window.confirm('Are you sure you want to delete this pump?')) return;

    const endpoint = currentView === 'residential' ? 'pumps' : 'agpumps';

    try {
      await axios.delete(`http://localhost:5001/api/${endpoint}/${id}`);
      fetchPumps(currentView, currentView === 'residential' ? setResidentialPumps : setAgriculturalPumps);
    } catch (err) {
      console.error('Error deleting pump:', err);
      setError('Error deleting pump.');
    }
  };

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>
      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
      </div>
      <div className="gallery-container">
        <div className="add-new-pump" onClick={() => setShowAddForm(true)}>
          <span className="plus-sign">+</span>
          <p className="add-text">Add New Pump</p>
        </div>
      </div>
      {showAddForm && (
        <div className="add-pump-form">
          <h2>Add New Pump</h2>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleAddPump}>
            <input
              type="text"
              name="id"
              value={newPumpData.id}
              onChange={handleInputChange}
              placeholder="Pump ID"
            />
            <input
              type="text"
              name="name"
              value={newPumpData.name}
              onChange={handleInputChange}
              placeholder="Pump Name"
              required
            />
            <input
              type="text"
              name="price"
              value={newPumpData.price}
              onChange={handleInputChange}
              placeholder="Pump Price"
              required
            />
            <textarea
              name="features"
              value={newPumpData.features}
              onChange={handleInputChange}
              placeholder="Features (comma-separated)"
              required
            />
            <input
              type="file"
              onChange={(e) => handleFileUpload(e, 'image')}
              required
            />
            <input
              type="file"
              onChange={(e) => handleFileUpload(e, 'rightImage')}
              required
            />
            <button type="submit">Add Pump</button>
            <button type="button" onClick={() => setShowAddForm(false)}>
              Cancel
            </button>
          </form>
        </div>
      )}
      <div className="gallery-container">
        <div className="pump-gallery">
          <h2>{currentView === 'residential' ? 'Residential' : 'Agricultural'} Pumps</h2>
          {(currentView === 'residential' ? residentialPumps : agriculturalPumps).map((pump) => (
            <div key={pump.id} className="pump-item">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p>{pump.name}</p>
                <p>{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`}>Read more</Link>
                <button onClick={() => handleDeletePump(pump.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminPortal;
