import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import Axios
import '../css/styl.css';

const Products = () => {
  const [pumps, setPumps] = useState([]);

  // Fetch pumps data from the backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/agpumps') // Update the endpoint for agricultural pumps
      .then(response => {
        setPumps(response.data); // Update state with fetched data
      })
      .catch(error => {
        console.error('There was an error fetching the pumps!', error);
      });
  }, []); // Empty dependency array to ensure the fetch only runs once

  const handleScroll = () => {
    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => {
      const rect = el.getBoundingClientRect();
      if (rect.top < window.innerHeight - 100) {
        el.classList.add('slide-in');
      }
    });
  };

  // Adding scroll event listener and cleanup
  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    // Cleanup on component unmount
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div>
      <div className="main-content" id="mainContent">
        <div className="product-info">
          <div className="container1">
            <div>
              <h2>Agricultural Products</h2>
            </div>
            <div>
              <p>We offer a wide range of Agricultural submersible pumps to suit your needs.</p>
            </div>
          </div>
        </div>


         {/* Agricultural Pumps Section */}
      <h2 className="section-title animate-on-scroll">Agricultural Submersible Pumps</h2>
      <div className="pump-gallery">
        {agriculturalPumps.length > 0 ? (
          agriculturalPumps.map((pump) => (
            <div key={pump.id} className="pump-item animate-on-scroll">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">${pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
              </div>
            </div>
          ))
        ) : (
          <p>Loading Agricultural Pumps...</p>
        )}
      </div>

        </div>
      </div>

  );
};

export default Products;
