import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../css/Admin.css'; // Ensure the CSS path is correct

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [image, setImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [error, setError] = useState(null);
  const [currentView, setCurrentView] = useState('residential');
  const [showAddForm, setShowAddForm] = useState(false); // Manage visibility of the add pump form
  const [newPumpData, setNewPumpData] = useState({
    id: '',
    name: '',
    price: '',
    features: '',
    image: '',
    rightImage: ''
  });

  // Fetch residential pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => setResidentialPumps(response.data))
      .catch((error) => console.error('Error fetching residential pumps:', error));
  }, []);

  // Fetch agricultural pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/agpumps')
      .then((response) => setAgriculturalPumps(response.data))
      .catch((error) => console.error('Error fetching agricultural pumps:', error));
  }, []);

// Handle image upload for 'image' field
// For multiple file upload
const handleMultipleImageUpload = async (e) => {
  const files = e.target.files;
  const formData = new FormData();

  for (let i = 0; i < files.length; i++) {
    formData.append('image', files[i]);
  }

  try {
    const response = await axios.post('http://localhost:5001/api/uploads/uploadMultiple', formData);
    console.log(response.data);
  } catch (err) {
    console.error('Error uploading images:', err);
  }
};

// For uploading 'rightImage' file
const handleRightImageUpload = async (e) => {
  const file = e.target.files[0];
  const formData = new FormData();
  formData.append('rightImage', file);

  try {
    const response = await axios.post('http://localhost:5001/api/uploads/uploadRightImage', formData);
    console.log(response.data);
  } catch (err) {
    console.error('Error uploading right image:', err);
  }
};



  // Handle new pump form input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPumpData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleAddPump = async (pumpData) => {
  try {
    const formData = new FormData();

    // Add basic fields
    formData.append('id', pumpData.id);
    formData.append('name', pumpData.name);
    formData.append('price', pumpData.price);

    // Add features as JSON string
    formData.append('features', JSON.stringify(pumpData.features));

    // Add image files
    if (pumpData.image) {
      formData.append('image', pumpData.image);
    }
    if (pumpData.rightImage) {
      formData.append('rightImage', pumpData.rightImage);
    }

    const response = await axios.post('http://localhost:5001/api/pumps', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    if (response.data.success) {
      console.log('Pump added successfully:', response.data.pump);
      // Handle success (e.g., show success message, refresh list)
    } else {
      throw new Error(response.data.message);
    }
  } catch (error) {
    console.error('Error adding new pump:', error);
    // Handle error (e.g., show error message to user)
    throw error;
  }
};

  // Handle delete pump functionality
  const handleDeletePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      // After deletion, reload the pumps
      const updatedPumps = residentialPumps.filter(pump => pump.id !== id);
      setResidentialPumps(updatedPumps);
    } catch (error) {
      console.error('Error deleting pump:', error);
      setError('Error deleting pump.');
    }
  };

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      {/* Sidebar for toggling views */}
      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
      </div>

      {/* Add New Pump Catalog */}
      <div className="gallery-container">
        <div className="add-new-pump" onClick={() => setShowAddForm(true)}>
          <span className="plus-sign">+</span>
          <p className="add-text">Add New Pump</p>
        </div>
      </div>

      {/* Add Pump Form (Visible when + sign is clicked) */}
      {showAddForm && (
        <div className="add-pump-form">
          <h2>Add New Pump</h2>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleAddPump}>
            <input
              type="string"
              name="id"
              value={newPumpData.id}
              onChange={handleInputChange}
              placeholder="Pump ID"
              required
            />
            <input
              type="string"
              name="name"
              value={newPumpData.name}
              onChange={handleInputChange}
              placeholder="Pump Name"
              required
            />
            <input
              type="string"
              name="price"
              value={newPumpData.price}
              onChange={handleInputChange}
              placeholder="Pump Price"
              required
            />
            <textarea
              name="features"
              value={newPumpData.features}
              onChange={handleInputChange}
              placeholder="Features (comma-separated)"
              required
            />
            <input type="file" onChange={handleMultipleImageUpload} required />
            <input type="file" onChange={handleRightImageUpload} required />
            <button type="submit">Add Pump</button>
            <button type="button" onClick={() => setShowAddForm(false)}>Cancel</button>
          </form>
        </div>
      )}

      {/* Pump Gallery */}
      <div className="gallery-container">
        <div className="pump-gallery">
          {currentView === 'residential' && (
            <>
              <h2>Residential Pumps</h2>
              {residentialPumps.length > 0 ? (
                residentialPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">
                        Read more
                      </Link>
                      <button className="delete-btn" onClick={() => handleDeletePump(pump.id)}>
                        Delete
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Residential Pumps...</p>
              )}
            </>
          )}

          {currentView === 'agricultural' && (
            <>
              <h2>Agricultural Pumps</h2>
              {agriculturalPumps.length > 0 ? (
                agriculturalPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">
                        Read more
                      </Link>
                      <button className="delete-btn" onClick={() => handleDeletePump(pump.id)}>
                        Delete
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Agricultural Pumps...</p>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPortal;
