import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css'; // Add custom CSS file for styling

const Details = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate();
    const [pump, setPump] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pumps, setPumps] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5001/api/agpumps')
            .then(response => setPumps(response.data))
            .catch(error => console.error('Error fetching agricultural pumps:', error));
    }, []);

    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`);
                if (!response.ok) {
                    throw new Error('Agricultural pump not found');
                }
                const data = await response.json();
                setPump(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchPumpDetails();
    }, [pumpId]);

    if (loading) return (
        <div className="loading-container">
            <div className="loading-message">Loading...</div>
        </div>
    );

    if (error) return (
        <div className="error-container">
            <div>{error}. Please go back to the <a href="/home" className="link">Home page</a></div>
        </div>
    );

    if (!pump) return (
        <div className="error-container">
            <div>Agricultural pump not found. Please go back to the <a href="/home" className="link">Home page</a></div>
        </div>
    );

    const pumpIds = pumps.map(pump => pump.id);
    const currentIndex = pumpIds.indexOf(pumpId);
    const prevPump = pumpIds[currentIndex - 1] || pumpIds[pumpIds.length - 1];
    const nextPump = pumpIds[currentIndex + 1] || pumpIds[0];

    const handleNavigation = (id) => {
        navigate(`/pumpDetails/${id}`);
    };

    return (
        <div className="details-container">
            <div className="details-wrapper">
                <div className="left-column">
                    <div className="main-image-container">
                        <img 
                            src={`http://localhost:5001${pump.image}`}
                            className="main-image"
                            alt={pump.name}
                            onError={(e) => {
                                e.target.onerror = null;
                                e.target.src = '/placeholder-image.png';
                            }}
                        />
                    </div>
                    <div className="product-info">
                        <h1 className="product-name">{pump.name}</h1>
                        <p className="product-price">
                            <strong>MRP:</strong> {pump.price} (Inclusive of all taxes)
                        </p>
                        <a 
                            href="https://lallytradingcompany.onrender.com"
                            className="enquiry-button"
                        >
                            Send Enquiry
                        </a>
                    </div>
                </div>

                <div className="right-column">
                    <div className="features-section">
                        <h2 className="features-title">Features</h2>
                        <ul className="features-list">
                            {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                pump.features.map((feature, index) => (
                                    <li key={index} className="feature-item">
                                        <span className="bullet-point">•</span>
                                        <span>{feature}</span>
                                    </li>
                                ))
                            ) : (
                                <li className="no-features">No features available.</li>
                            )}
                        </ul>
                    </div>
                    <div className="right-view-image-container">
                        <img 
                            src={`http://localhost:5001${pump.rightImage}`}
                            className="right-view-image"
                            alt="Right view of pump"
                            onError={(e) => {
                                e.target.onerror = null;
                                e.target.src = '/placeholder-image.png';
                            }}
                        />
                    </div>
                </div>
            </div>

            <div className="navigation-buttons">
                <button 
                    onClick={() => handleNavigation(prevPump)}
                    className="nav-button prev-button"
                >
                    ←
                </button>
                <button 
                    onClick={() => handleNavigation(nextPump)}
                    className="nav-button next-button"
                >
                    →
                </button>
            </div>
        </div>
    );
};

export default Details;
