import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]); // Default to an empty array
  const [agPumps, setAgPumps] = useState([]); // Default to an empty array
  const [newPump, setNewPump] = useState({
    name: '',
    price: '',
    features: '',
    previousId: '',
    nextId: ''
  });
  const [newAgPump, setNewAgPump] = useState({
    name: '',
    price: '',
    features: '',
    previousId: '',
    nextId: ''
  });
  const [image, setImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [error, setError] = useState('');
  const [isAdding, setIsAdding] = useState(false); // Toggle for adding new pump

  useEffect(() => {
    // Fetch pumps and agPumps
    const fetchPumps = async () => {
      try {
        const pumpResponse = await axios.get('http://localhost:5001/api/pumps');
        setPumps(pumpResponse.data.pumps || []); // Ensure it's an array

        const agPumpResponse = await axios.get('http://localhost:5001/api/agpumps');
        setAgPumps(agPumpResponse.data.agpumps || []); // Ensure it's an array
      } catch (err) {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  const handleImageUpload = async (e) => {
    const files = e.target.files;
    const formData = new FormData();

    if (files.length > 0) {
      formData.append('images', files[0]);
      formData.append('images', files[1]);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.filePaths[0]);
        setRightImage(response.data.filePaths[1]);
      } catch (err) {
        console.error('Error uploading images:', err);
        setError('Error uploading images.');
      }
    }
  };

  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.features || !image || !rightImage) {
      setError('Please fill all fields and upload images.');
      return;
    }

    try {
      const pumpData = { ...newPump, image, rightImage };
      const response = await axios.post('http://localhost:5001/api/pumps', pumpData);
      setPumps([...pumps, response.data.pump]);
      setNewPump({ name: '', price: '', features: '', previousId: '', nextId: '' });
      setImage(null);
      setRightImage(null);
      setError('');
      setIsAdding(false); // Close Add Pump form
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  const handleAddAgPump = async () => {
    if (!newAgPump.name || !newAgPump.price || !newAgPump.features || !image || !rightImage) {
      setError('Please fill all fields and upload images.');
      return;
    }

    try {
      const agPumpData = { ...newAgPump, image, rightImage };
      const response = await axios.post('http://localhost:5001/api/agpumps', agPumpData);
      setAgPumps([...agPumps, response.data.agPump]);
      setNewAgPump({ name: '', price: '', features: '', previousId: '', nextId: '' });
      setImage(null);
      setRightImage(null);
      setError('');
      setIsAdding(false); // Close Add AgPump form
    } catch (err) {
      console.error('Error adding agricultural pump:', err);
      setError('Error adding agricultural pump.');
    }
  };

  const handleRemovePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      setPumps(pumps.filter((pump) => pump._id !== id));
    } catch (err) {
      console.error('Error removing pump:', err);
      setError('Error removing pump.');
    }
  };

  const handleRemoveAgPump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/agpumps/${id}`);
      setAgPumps(agPumps.filter((agPump) => agPump._id !== id));
    } catch (err) {
      console.error('Error removing agricultural pump:', err);
      setError('Error removing agricultural pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {/* Add New Pump Form */}
      {isAdding ? (
        <div>
          <h2>Add New Pump</h2>
          <input type="text" placeholder="Name" value={newPump.name} onChange={(e) => setNewPump({ ...newPump, name: e.target.value })} />
          <input type="text" placeholder="Price" value={newPump.price} onChange={(e) => setNewPump({ ...newPump, price: e.target.value })} />
          <input type="text" placeholder="Features (comma separated)" value={newPump.features} onChange={(e) => setNewPump({ ...newPump, features: e.target.value })} />
          <input type="text" placeholder="Previous ID" value={newPump.previousId} onChange={(e) => setNewPump({ ...newPump, previousId: e.target.value })} />
          <input type="text" placeholder="Next ID" value={newPump.nextId} onChange={(e) => setNewPump({ ...newPump, nextId: e.target.value })} />
          <input type="file" onChange={handleImageUpload} multiple />
          <button onClick={handleAddPump}>Add Pump</button>
          <button onClick={() => setIsAdding(false)}>Cancel</button>
        </div>
      ) : (
        <div className="add-pump-btn" onClick={() => setIsAdding(true)}>
          <span>+</span> Add Pump
        </div>
      )}

      {/* Pumps Grid */}
      <h2>Pumps</h2>
      <div className="pump-gallery">
        {Array.isArray(pumps) && pumps.length > 0 ? (
          pumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <img src={pump.image} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read More</Link>
                <button onClick={() => handleRemovePump(pump._id)} className="delete-btn">Delete</button>
              </div>
            </div>
          ))
        ) : (
          <div>No pumps available. Click "+" to add a pump.</div>
        )}
      </div>

      {/* Agricultural Pumps Grid */}
      <h2>Agricultural Pumps</h2>
      <div className="pump-gallery">
        {Array.isArray(agPumps) && agPumps.length > 0 ? (
          agPumps.map((agPump) => (
            <div key={agPump._id} className="pump-item">
              <img src={agPump.image} alt={agPump.name} />
              <div className="pump-info">
                <p className="pump-name">{agPump.name}</p>
                <p className="pump-price">{agPump.price}</p>
                <Link to={`/pumpDetail/${agPump.id}`} className="read-more-btn">Read More</Link>
                <button onClick={() => handleRemoveAgPump(agPump._id)} className="delete-btn">Delete</button>
              </div>
            </div>
          ))
        ) : (
          <div>No agricultural pumps available. Click "+" to add one.</div>
        )}
      </div>
    </div>
  );
};

export default AdminPortal;
