import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/styles.css'; // Ensure your CSS file is correct

function Home() {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const images = [require('../images/PUMP.jpg')]; // Your image for the main slider

  // Automatic sliding for main image
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  // Fetch pumps data from the backend API
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/pumps');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('Fetched pump data: ', data); // Log pump data to check
        setPumps(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError('Failed to load pumps data');
        setLoading(false);
      }
    };
    fetchPumps();
  }, []);

  console.log(pumps); // Log pumps state to verify if data is set correctly

  return (
    <div className="main-content">
      {/* Main Image Slider */}
      <div className="image-section1 animate-on-scroll">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      {/* Description Section */}
      <div className="b animate-on-scroll">
        <center>
          <p className="b2">
            Explore our range of Agricultural submersible pumps <br />
            The product range covers a wide application such as agriculture,
            water supply to townships, Industries, sewage, and drainage.
          </p>
          <Link to="/products" className="btn">View Products</Link>
        </center>
      </div>

      {/* Pumps Section */}
      <h2 className="section-title animate-on-scroll">Residential Submersible Pump</h2>
      {loading ? (
        <div>Loading pumps...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div className="pump-gallery" id="pumpGallery">
          {pumps.length > 0 ? (
            pumps.map((pump) => (
              <div key={pump._id} className="pump-item animate-on-scroll">
                <p>{pump.name}</p> {/* Temporarily displaying just the name */}
                <p>{`MRP ₹${pump.price}`}</p>
              </div>
            ))
          ) : (
            <div>No pumps available.</div>
          )}
        </div>
      )}

      {/* Additional Sections: About, Client Trust, Product Display */}
      {/* Add these sections here as shown in the previous responses */}
    </div>
  );
}

export default Home;
