import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const PumpDetail = () => {
  const { pumpId } = useParams();
  const [pump, setPump] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found.');
        }
      })
      .catch(() => setError('Failed to fetch pump details.'));
  }, [pumpId]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!pump) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{pump.name}</h1>
      <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
      <p>{pump.price}</p>
      <ul>
        {pump.features.map((feature, idx) => (
          <li key={idx}>{feature}</li>
        ))}
      </ul>
    </div>
  );
};

export default PumpDetail;
