import React from 'react';
import { useParams } from 'react-router-dom';
import pumps from '../data/pumps'; // Adjust the path as necessary

const PumpDetail = () => {
    const { id } = useParams(); // Get the id from the URL
    const pump = pumps.find(p => p.id === id); // Find the pump by id

    if (!pump) {
        return <h2>Pump not found!</h2>; // Handle case where pump is not found
    }

    return (
        <div>
            <h1>{pump.name}</h1>
            <img src={pump.image} alt={pump.name} />
            <p>Price: ${pump.price}</p>
        </div>
    );
};

export default PumpDetail;
