import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '', link: '' });
  const [image, setImage] = useState(null);
  const [error, setError] = useState('');

  // Fetch existing pumps
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError('Failed to fetch pumps.');
      }
    };
    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('image', file);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.imageUrl); // Get the uploaded image URL
      } catch (err) {
        console.error('Error uploading image:', err);
        setError('Error uploading image.');
      }
    }
  };

  // Add new pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !image) {
      setError('Please fill all fields and upload an image.');
      return;
    }

    try {
      const pump = { ...newPump, image };
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data]);
      setNewPump({ name: '', price: '', link: '' });
      setImage(null);
      setError('');
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  // Remove pump
  const handleRemovePump = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      if (response.data.success) {
        setPumps(pumps.filter((pump) => pump._id !== id));
      } else {
        setError('Failed to delete pump: ' + response.data.message);
      }
    } catch (err) {
      console.error('Error removing pump:', err);
      setError('Error removing pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <h2>Add New Pump</h2>
      <input
        type="text"
        placeholder="Name"
        value={newPump.name}
        onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newPump.price}
        onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} />
      <button onClick={handleAddPump}>Add Pump</button>

      <h2>Existing Pumps</h2>
      {pumps.length === 0 ? (
        <p>No pumps available.</p>
      ) : (
        pumps.map((pump) => (
          <div key={pump._id}>
            <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} width="100" />
            <p>{pump.name}</p>
            <p>{pump.price}</p>
            <button onClick={() => handleRemovePump(pump._id)}>Remove</button>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPortal;
