import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import Axios
import prod from '../images/prod.png';
import pump2 from '../images/pump2.png';
import bg1 from '../images/PUMP.jpg';

//import PumpGallery from './PumpGallery';

function Home() {
  const [pumps, setPumps] = useState([]); // State to store fetched pumps data

  // Fetch pumps data from the backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/pumps')
      .then(response => {
        setPumps(response.data); // Update state with fetched data
      })
      .catch(error => {
        console.error('There was an error fetching the pumps!', error);
      });
  }, []);



  // Add scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('slide-in');
          }
        });
      },
      { threshold: 0.1 }
    );

    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <div className="main-content">
      <div className="image-section1 animate-on-scroll">
        <div className="slider">



      <div className="pump-gallery" id="pumpGallery">
        {pumps.length > 0 ? (
          pumps.map((pump) => (
            <div key={pump.id} className="pump-item animate-on-scroll">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />

              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id.toString()}`} className="read-more-btn">Read more</Link>
              </div>
            </div>
          ))
        ) : (
          <p>Loading pumps...</p>
        )}
      </div>
</div>
</div>
</div>



  );
}

export default Home;
