import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
  const { pumpId } = useParams(); // Extract the pumpId from the URL
  const navigate = useNavigate();
  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pump details by ID from backend API
  useEffect(() => {
    fetch(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPump(data.pump); // Set the pump data
        } else {
          setError('Pump not found');
        }
        setLoading(false); // Stop loading
      })
      .catch((err) => {
        console.error('Error fetching pump details:', err);
        setError('Failed to load pump details');
        setLoading(false); // Stop loading even in case of an error
      });
  }, [pumpId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Handle navigation between pumps
  const handleNavigation = (id) => {
    navigate(`/pumpDetail/${id}`);
  };

  return (
    <div className="body">
      {/* Product Description */}
      <div className="product-description">
        <img
          src={`http://localhost:5001/uploads/${pump.image}`} // Display the image from the uploads folder
          className="product-image"
          alt={pump.name}
        />
        <div>
          <h1>{pump.name}</h1>
          <p>
            <strong>MRP:</strong> {pump.price} (Inclusive of all taxes)
          </p>

          <div className="features">
            <h2>Features</h2>
            <ul>
              {pump.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>

          <a href="https://lallytradingcompany.onrender.com" className="buy-button">
            Send Enquiry
          </a>
        </div>
      </div>

      {/* Image for right view of the pump */}
      <div className="chart">
        <img
          src={`http://localhost:5001/uploads/${pump.rightImage}`}
          alt="Right view of pump"
        />
      </div>

      {/* Navigation Buttons */}
      <div className="navigation-buttons">
        <button
          className="nav-btn left-btn"
          onClick={() => handleNavigation(pump.previousId)}
        >
          &#8592; {/* Left Arrow (back) */}
        </button>
        <button
          className="nav-btn right-btn"
          onClick={() => handleNavigation(pump.nextId)}
        >
          &#8594; {/* Right Arrow (forward) */}
        </button>
      </div>
    </div>
  );
};

export default PumpDetail;
