import React from 'react';
import '../components/read.css';
import pumps from '../data/pumps'
const PumpDetail = ({ pumpId }) => {
    const pump = pumps.find(p => p.id === pumpId);

    return (
        <div className="product-description">
            <h2>{pump.name}</h2>
            <div className="product-image">
                <img src={pump.image} alt={`${pump.name}`} />
            </div>
            <div className="features">
                <h3>Features:</h3>
                <ul>
                    {pump.features.map((feature, index) => (
                        <li key={index}>{feature}</li>
                    ))}
                </ul>
            </div>
            <div className="image-right">
                <img src={pump.rightImage} alt={`Right view of ${pump.name}`} />
            </div>
            <a href="#" className="buy-button">Buy Now</a>
        </div>
    );
};

export default PumpDetail;
