import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; // Import custom CSS for styling
import axios from 'axios';

const PumpDetails = () => {
    const { pumpId } = useParams(); // Extract pumpId from URL
    const navigate = useNavigate();

    const [pump, setPump] = useState(null); // State to hold pump data
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state
    const [pumps, setPumps] = useState([]); // State for list of pump IDs

    // Fetch all pumps data to handle navigation
    useEffect(() => {
        axios.get('http://localhost:5001/api/agpumps')
            .then(response => {
                console.log('Response from /api/agpumps:', response.data); // Full response for debugging
                if (response.data && response.data.agPumps) {
                    setPumps(response.data.agPumps); // Store the fetched pumps in state
                } else {
                    console.error('No agPumps in the response');
                    setError('No pumps data available');
                }
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching pumps:', error);
                setError('Error fetching pumps list');
                setLoading(false);
            });
    }, []);

    // Fetch pump details based on the pumpId
    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                console.log('Fetching details for pumpId:', pumpId); // Log the pumpId for debugging

                // Call the API to fetch pump data based on the ID from the URL
                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`);

                if (!response.ok) {
                    throw new Error('Pump not found');
                }

                const data = await response.json();
                console.log('Fetched pump data:', data); // Log the fetched data

                setPump(data.agPump); // Update state with the fetched pump data
                setLoading(false); // Set loading to false once data is fetched
            } catch (err) {
                setError('Error fetching pump details');
                setLoading(false);
            }
        };

        fetchPumpDetails(); // Call the function to fetch pump details
    }, [pumpId]); // Re-run the effect when pumpId changes

    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    if (error) {
        return <div>{error}. Please go back to the <a href="/Home">agricultural pumps page</a>.</div>; // Show error message if pump not found
    }

    if (!pump) {
        return <div>Pump not found. Please go back to the <a href="/Home">agricultural pumps page</a>.</div>; // Fallback if no pump data
    }

    // Ensure that pumps array is populated and map pump IDs
    const pumpIds = pumps.map(p => p.id); // Ensure that pumps is always an array
    console.log('pumpIds:', pumpIds); // Debugging line

    // Find the index of the current pump in the pumpIds array
    const currentIndex = pumpIds.indexOf(pumpId);
    console.log('currentIndex:', currentIndex); // Debugging line

    // Handle navigation to the previous or next pump with safeguards against undefined
    const handleNavigation = (id) => {
        if (!id) {
            console.log('No valid ID for navigation'); // Debugging
            return;
        }
        console.log('Navigating to pump with ID:', id); // Debugging
        navigate(`/pumpDetails/${id}`);
    };

    // Ensure the prevPump and nextPump IDs are valid
    const prevPump = pumpIds[(currentIndex - 1 + pumpIds.length) % pumpIds.length]; // Wrap around to the last pump if out of bounds
    const nextPump = pumpIds[(currentIndex + 1) % pumpIds.length]; // Wrap around to the first pump if out of bounds

    // Debugging navigation values
    console.log('prevPump:', prevPump);
    console.log('nextPump:', nextPump);

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                {/* Ensure image URL is correct */}
                <img 
                    src={`http://localhost:5001/uploads/${pump.image}`} 
                    className="product-image" 
                    alt={pump.name} 
                />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                pump.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))
                            ) : (
                                <li>No features available.</li> // Fallback message if features are empty or undefined
                            )}
                        </ul>
                    </div>

                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Image for right view of the pump */}
            <div className="chart">
                <img 
                    src={`http://localhost:5001/uploads/${pump.rightImage}`} 
                    alt="Right view of pump" 
                />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump)} 
                    disabled={!prevPump}
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump)} 
                    disabled={!nextPump}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>
        </div>
    );
};

export default PumpDetails;
