import React, { useEffect, useState } from 'react';
import v4Image from '../images/v4.png';
import v42Image from '../images/v42.png';
import v3Image from '../images/v3.png';
import v32Image from '../images/v32.png';
import v40Image from '../images/v40.png';
import owImage from '../images/ow.png';
import ow1Image from '../images/OW1.png';

import '../css/styles.css';

function Home() {
  const images = [v4Image, v42Image, v3Image, v32Image, v40Image, owImage, ow1Image];
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % images.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(interval); // Clean up interval on component unmount
  }, []);

  return (
    <div className="main-content">
      <div className="image-section">
        <div className="slider-container">
          <div className="slider-images" style={{ transform: `translateX(-${currentImage * 100}%)` }}>
            {images.map((image, index) => (
              <img key={index} src={image} alt={`slide ${index}`} className="header-image" />
            ))}
          </div>
        </div>
      </div>

      <h1>Welcome to Our Website</h1>
      <p>Explore our wide range of pumps and more!</p>
    </div>
  );
}

export default Home;
