import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
  const { pumpId } = useParams(); // Extract pumpId from URL
  const navigate = useNavigate();
  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Make sure pumpId is available in the URL
    if (!pumpId) {
      setError('Invalid pump ID');
      setLoading(false);
      return;
    }

    useEffect(() => {
  setPump({
    name: 'Sample Pump',
    price: '1000',
    image: 'sample-pump.jpg',
    features: ['Feature 1', 'Feature 2'],
    rightImage: 'right-sample.jpg',
  });
  setLoading(false);
}, []);


    // Fetch pump details
    fetch(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching pump details:', err);
        setError('Failed to load pump details');
        setLoading(false);
      });
  }, [pumpId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="body">
      <div className="product-description">
        <img
          src={`http://localhost:5001/uploads/${pump.image}`} // Correctly load the image
          alt={pump.name}
          className="product-image"
        />
        <div>
          <h1>{pump.name}</h1>
          <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>
          <div className="features">
            <h2>Features</h2>
            <ul>
              {pump.features && pump.features.length > 0 ? (
                pump.features.map((feature, index) => <li key={index}>{feature}</li>)
              ) : (
                <li>No features available</li>
              )}
            </ul>
          </div>
          <a href="https://lallytradingcompany.onrender.com" className="buy-button">
            Send Enquiry
          </a>
        </div>
      </div>

      <div className="chart">
        <img
          src={`http://localhost:5001/uploads/${pump.rightImage}`}
          alt="Right view of pump"
        />
      </div>

      {/* Navigation Buttons */}
      <div className="navigation-buttons">
        <button
          className="nav-btn left-btn"
          onClick={() => navigate(`/pumpDetail/${pump.previousId}`)}
        >
          &#8592; {/* Left Arrow (back) */}
        </button>
        <button
          className="nav-btn right-btn"
          onClick={() => navigate(`/pumpDetail/${pump.nextId}`)}
        >
          &#8594; {/* Right Arrow (forward) */}
        </button>
      </div>
    </div>
  );
};

export default PumpDetail;
