// AdminPortal.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({
    name: '',
    price: '',
    features: '',
    image: null,
    tableImage: null,
  });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  // Fetch pumps from the database
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => {
        setPumps(response.data.pumps);
      })
      .catch((err) => {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps');
      });
  }, [message]);

  const handleAddPump = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', newPump.name);
    formData.append('price', newPump.price);
    formData.append('features', newPump.features);

    // Append files
    if (newPump.image) {
      formData.append('image', newPump.image);
    }
    if (newPump.tableImage) {
      formData.append('tableImage', newPump.tableImage);
    }

    axios
      .post('http://localhost:5001/api/pumps', formData)
      .then((response) => {
        setMessage('Pump added successfully!');
        setPumps([...pumps, response.data.pump]);
        setNewPump({ name: '', price: '', features: '', image: null, tableImage: null });
      })
      .catch((err) => {
        console.error('Error adding pump:', err);
        setError('Failed to add pump');
      });
  };


  const handleDeletePump = (pumpId) => {
    axios
      .delete(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => {
        setMessage('Pump deleted successfully!');
        setPumps(pumps.filter((pump) => pump._id !== pumpId));
      })
      .catch((err) => {
        setError('Failed to delete pump');
      });
  };

  return (
    <div>
      <h2>Admin Portal</h2>

      {message && <div>{message}</div>}
      {error && <div>{error}</div>}

      <form onSubmit={handleAddPump}>
        <h3>Add New Pump</h3>
        <input
          type="text"
          placeholder="Name"
          value={newPump.name}
          onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={newPump.price}
          onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Features (comma separated)"
          value={newPump.features}
          onChange={(e) => setNewPump({ ...newPump, features: e.target.value })}
        />
        <input
          type="file"
          onChange={(e) => setNewPump({ ...newPump, image: e.target.files[0] })}
        />
        <input
          type="file"
          onChange={(e) => setNewPump({ ...newPump, tableImage: e.target.files[0] })}
        />
        <button type="submit">Add Pump</button>
      </form>

      <h3>Existing Pumps</h3>
      <ul>
        {pumps.map((pump) => (
          <li key={pump._id}>
            <h4>{pump.name}</h4>
            <p>Price: {pump.price}</p>
            <button onClick={() => handleDeletePump(pump._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPortal;
