import React from 'react';
import { Link } from 'react-router-dom';
import '../css/Admin.css';

const AgriculturalPumps = ({ pumps, handleDeletePump }) => {
  return (
    <div>
      <h2>Agricultural Pumps</h2>
      {pumps.length > 0 ? (
        <div className="pump-gallery">
          {pumps.map((pump) => (
            <div key={pump.id} className="pump-item">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} className="pump-image" />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">Read more</Link>
                <button className="delete-btn" onClick={() => handleDeletePump(pump.id, 'agricultural')}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading Agricultural Pumps...</p>
      )}
    </div>
  );
};

export default AgriculturalPumps;
