// src/components/PumpDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const PumpDetail = () => {
  const { pumpId } = useParams(); // Get the pump ID from the URL parameters
  const [pump, setPump] = useState(null); // State to store the pump details
  const [error, setError] = useState(''); // State for error handling
  const [loading, setLoading] = useState(true); // State for loading indicator

  useEffect(() => {
    // Fetch pump details from the API
    const fetchPumpDetails = async () => {
      try {
        console.log('Pump ID:', pumpId);  // Should be "v4" or similar, not MongoDB _id
const response = await fetch(`http://localhost:5001/api/pumps/${pumpId}`);
;
; // Use the pumpId to fetch data
        const data = await response.json();

        if (response.ok && data.success) {
          setPump(data.pump); // Save pump data in state
        } else {
          setError(data.message || 'Pump not found or an error occurred.');
        }
      } catch (err) {
        setError('Failed to fetch pump details.');
      } finally {
        setLoading(false);
      }
    };

    fetchPumpDetails();
  }, [pumpId]);

  // Conditional rendering for loading, errors, and pump data
  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!pump) return <p>Pump details not available.</p>;

  return (
    <div className="pump-detail">
      <h1>{pump.name}</h1>

      {/* Display Pump Image */}
      <img
        src={`http://localhost:5001/uploads/${pump.image}`}
        alt={pump.name}
        style={{ width: '100%', height: 'auto', maxWidth: '500px', marginBottom: '20px' }}
      />

      {/* Pump Price */}
      <p><strong>Price:</strong> {pump.price}</p>

      {/* Technical Details */}
      {pump.rightImage && (
        <div style={{ margin: '20px 0' }}>
          <strong>Technical Details:</strong>
          <img
            src={`http://localhost:5001/uploads/${pump.rightImage}`}
            alt="Technical Details"
            style={{ width: '100%', height: 'auto', maxWidth: '500px' }}
          />
        </div>
      )}

      {/* Features List */}
      <h3>Features:</h3>
      {pump.features && pump.features.length > 0 ? (
        <ul>
          {pump.features.map((feature, idx) => (
            <li key={idx}>{feature}</li>
          ))}
        </ul>
      ) : (
        <p>No features available for this pump.</p>
      )}

      {/* Back Button */}
      <Link to="/" style={{ marginTop: '20px', display: 'inline-block', textDecoration: 'none', color: '#007bff' }}>
        Back to Pump List
      </Link>
    </div>
  );
};

export default PumpDetail;
