import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [newPump, setNewPump] = useState({
    name: '',
    price: '',
    features: '',
    previousId: '',
    nextId: ''
  });
  const [image, setImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [error, setError] = useState('');

  // Fetch residential and agricultural pumps data from the backend
  useEffect(() => {
    // Fetch residential pumps
    axios.get('http://localhost:5001/api/pumps')
      .then(response => {
        console.log("Residential Pumps Fetched:", response.data);
        setResidentialPumps(response.data);
      })
      .catch(error => {
        console.error('Error fetching residential pumps:', error);
        setError('Failed to fetch residential pumps.');
      });

    // Fetch agricultural pumps
    axios.get('http://localhost:5001/api/agpumps')
      .then(response => {
        console.log("Agricultural Pumps Fetched:", response.data);
        setAgriculturalPumps(response.data);
      })
      .catch(error => {
        console.error('Error fetching agricultural pumps:', error);
        setError('Failed to fetch agricultural pumps.');
      });
  }, []);

  const handleImageUpload = async (e) => {
    const files = e.target.files;
    const formData = new FormData();

    if (files.length > 0) {
      formData.append('images', files[0]);
      formData.append('images', files[1]);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.filePaths[0]);
        setRightImage(response.data.filePaths[1]);
      } catch (err) {
        console.error('Error uploading images:', err);
        setError('Error uploading images.');
      }
    }
  };

  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.features || !image || !rightImage) {
      setError('Please fill all fields and upload images.');
      return;
    }

    try {
      const pumpData = { ...newPump, image, rightImage };
      const response = await axios.post('http://localhost:5001/api/pumps', pumpData);
      setResidentialPumps([...residentialPumps, response.data.pump]);
      setNewPump({ name: '', price: '', features: '', previousId: '', nextId: '' });
      setImage(null);
      setRightImage(null);
      setError('');
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  const handleRemovePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      setResidentialPumps(residentialPumps.filter((pump) => pump._id !== id));
    } catch (err) {
      console.error('Error removing pump:', err);
      setError('Error removing pump.');
    }
  };

  const handleRemoveAgPump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/agpumps/${id}`);
      setAgriculturalPumps(agriculturalPumps.filter((pump) => pump._id !== id));
    } catch (err) {
      console.error('Error removing agricultural pump:', err);
      setError('Error removing agricultural pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {/* Residential Pumps Grid */}
      <h2>Residential Pumps</h2>
      <div className="pump-gallery">
        {Array.isArray(residentialPumps) && residentialPumps.length > 0 ? (
          residentialPumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <p>Name: {pump.name}</p> {/* Debugging pump name */}
              <p>Image: {`http://localhost:5001/uploads/${pump.image}`}</p> {/* Debugging image URL */}
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read More</Link>
                <button onClick={() => handleRemovePump(pump._id)} className="delete-btn">Delete</button>
              </div>
            </div>
          ))
        ) : (
          <div>No residential pumps available. Click "+" to add a pump.</div>
        )}
      </div>

      {/* Agricultural Pumps Grid */}
      <h2>Agricultural Pumps</h2>
      <div className="pump-gallery">
        {Array.isArray(agriculturalPumps) && agriculturalPumps.length > 0 ? (
          agriculturalPumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <p>Name: {pump.name}</p> {/* Debugging pump name */}
              <p>Image: {`http://localhost:5001/uploads/${pump.image}`}</p> {/* Debugging image URL */}
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">Read More</Link>
                <button onClick={() => handleRemoveAgPump(pump._id)} className="delete-btn">Delete</button>
              </div>
            </div>
          ))
        ) : (
          <div>No agricultural pumps available. Click "+" to add one.</div>
        )}
      </div>

      {/* Add New Pump Form */}
      <h2>Add New Pump</h2>
      <input type="text" placeholder="Name" value={newPump.name} onChange={(e) => setNewPump({ ...newPump, name: e.target.value })} />
      <input type="text" placeholder="Price" value={newPump.price} onChange={(e) => setNewPump({ ...newPump, price: e.target.value })} />
      <input type="text" placeholder="Features (comma separated)" value={newPump.features} onChange={(e) => setNewPump({ ...newPump, features: e.target.value })} />
      <input type="text" placeholder="Previous ID" value={newPump.previousId} onChange={(e) => setNewPump({ ...newPump, previousId: e.target.value })} />
      <input type="text" placeholder="Next ID" value={newPump.nextId} onChange={(e) => setNewPump({ ...newPump, nextId: e.target.value })} />
      <input type="file" onChange={handleImageUpload} multiple />
      <button onClick={handleAddPump}>Add Pump</button>
    </div>
  );
};

export default AdminPortal;
